    public void updateProcesses(List<RTProcess> processList){
        if(this.processTable.getRowCount() != 0)
            for (int i = processTable.getRowCount()-1; i >= 0 ; i--) {
                ((DefaultTableModel)processTable.getModel()).removeRow(i);
            }

        for (RTProcess process :
                processList) {
            ((DefaultTableModel) processTable.getModel()).addRow(new Object[]{
                    process.getId(), process.getCycle(), process.getExecutiontTime(), process.getDeadline(),
                    process.getExecutedPeriods(), process.getMissedPeriods(), process.getMissedDealines()
            });
        }
}

